import { Dropdown, DropdownItem, Menu,Search } from "semantic-ui-react";
import { Link } from 'react-router-dom';
import React from 'react';
import Dropp from './component/Dropp';

import firebase from './utils/firebase';

function Header(){
    const [user, setUser] = React.useState(null);
    React.useEffect(() =>{
        firebase.auth().onAuthStateChanged((currentUser) =>{
            setUser(currentUser);
        });
    },[]);
    return <Menu>
        <Menu.Item as={Link} to="/">
            分你所析
        </Menu.Item>
        <Menu.Item>
            <Search />
        </Menu.Item>
        <Menu.Menu position='right'>
            {user ? (
            <>
                <Menu.Item href="https://www.antgroup.com">
                聯絡我們
                </Menu.Item>
                <Menu.Item>
                    <Dropp/>
                </Menu.Item>
                {/*<Menu.Item>
                    <Dropdown title = '分析'>
                        <Dropdown.Item>文章分析</Dropdown.Item>
                        <Dropdown.Item>歷史紀錄</Dropdown.Item>
                    </Dropdown>
                </Menu.Item>
                不知道怎麼寫*/}
                <Menu.Item as={Link} to="/my">
                會員
                </Menu.Item>
                <Menu.Item onClick={() => firebase.auth().signOut()}>
                登出
                </Menu.Item>
            </>
            ) : (
                <Menu.Item as={Link} to="/signin">
                註冊/登入
                </Menu.Item>
            )}
        </Menu.Menu>
    </Menu>
}

export default Header
